package arrays303;

public class MethodExample2 {
	public static int sqrs( int x) {
		return x*x;
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int result;
		 int inputValue=10;
		 result = sqrs(inputValue);
		 System.out.println("Squared value  is: " + result);
	}

}
